import React from 'react';
import './App.css';
import Button from '@material-ui/core/Button';
// import Paper from '@material-ui/core/Paper';
// import InputBase from '@material-ui/core/InputBase';
// import SearchIcon from '@material-ui/icons/Search';
// import { ToastContainer, toast } from 'react-toastify';
// import 'react-toastify/dist/ReactToastify.css';
// import { css } from 'glamor';
// import IconButton from '@material-ui/core/IconButton';


class App extends React.Component{

  constructor(props) {
        super(props);
          this.state = {
            imageURL: '',
            picture: {}
          };
         // this.onDrop = this.onDrop.bind(this);
         this.change = this.change.bind(this);
         this.handleUploadImage = this.handleUploadImage.bind(this);
    }

    change(e){
      console.log("Change", e);
      // var file = this.refs.file.files[0];
      var reader = new FileReader();
      // var url = reader.readAsDataURL(file);
    }

  // // When a picture is uploaded
  // onDrop(new_picture) {
  //     // Set the picture
  //     this.setState({
  //         picture: new_picture,
  //     });
  //
  //     console.log(new_picture);

  // }

  handleUploadImage(e) {
    e.preventDefault();

    const data = new FormData();
    data.append('file', this.uploadInput.files[0]);
    // data.append('filename', this.fileName.value);

    fetch('http://0.0.0.0:5000/upload', {
      method: 'PUT',
      body: data
    }).then((response) => {
      response.json().then((r) => {
        console.log(r);
      });
      // response.json().then((body) => {
      //   this.setState({ imageURL: `http://localhost:8000/${body.file}` });
      // });
    });
  }


  render(){
    console.log("Render app");
    let img;
    if (this.uploadInput != {}){
      img = <img src={ require(this.uploadInput) } />
    }
    else{
      img = <p>Aric</p>
    }

    return(
      <div>
        <h1>Traffic Sign Recognition</h1>
        <p> Currently Supports: Stop Sign, Rail Road Crossing, Yield, One Way, Handicapped Parking, Pedestrian Crossing, Wrong Way</p>

        <div>
          <input onChange={this.change} ref={(ref) => { this.uploadInput = ref; }} type="file" />
          <Button onClick={this.handleUploadImage}>Classify</Button>
        </div>
        <div>
          {img}
          {/* }<img src={URL.createObjectURL('./stop_sign_copy.png')} /> */}
        </div>
        {/* Comment */}

      </div>
    )
  }
}
