import React from 'react';
import './App.css';
import Button from '@material-ui/core/Button';
// import Paper from '@material-ui/core/Paper';
// import InputBase from '@material-ui/core/InputBase';
// import SearchIcon from '@material-ui/icons/Search';
// import { ToastContainer, toast } from 'react-toastify';
// import 'react-toastify/dist/ReactToastify.css';
// import { css } from 'glamor';
// import IconButton from '@material-ui/core/IconButton';


class App extends React.Component{

  constructor(props) {
        super(props);
          this.state = {
            mageURL: '',
            picture: {}
          };
         this.onDrop = this.onDrop.bind(this);
         this.handleUploadImage = this.handleUploadImage.bind(this);
    }

  // When a picture is uploaded
  onDrop(new_picture) {
      // Set the picture
      this.setState({
          picture: new_picture,
      });

      console.log(new_picture);
      var file = this.refs.file.files[0];
    var reader = new FileReader();
    var url = reader.readAsDataURL(file);
  }

  handleUploadImage(e) {
    console.log("HANDLE");
    e.preventDefault();

    const data = new FormData();
    data.append('file', this.uploadInput.files[0]);
    // data.append('filename', this.fileName.value);

    fetch('http://0.0.0.0:5000/upload', {
      method: 'PUT',
      body: data
    }).then((response) => {
      response.json().then((r) => {
        console.log(r);
      });
      // response.json().then((body) => {
      //   this.setState({ imageURL: `http://localhost:8000/${body.file}` });
      // });
    });
  }


  render(){
    console.log("Render app");
    return(
      <div>
        <h1>Traffic Sign Recognition</h1>
        <p> Currently Supports: Stop Sign, Rail Road Crossing, Yield, One Way, Handicapped Parking, Pedestrian Crossing, Wrong Way</p>

        <div>
          <input ref={(ref) => { this.uploadInput = ref; }} type="file" />
          <Button onClick={this.handleUploadImage}>Classify</Button>
        </div>
        <div>
          <img src={ require('./stop_sign_copy.png') } />
        </div>

      </div>
    )
  }
}
